        if ($this->form_validation->run()) {
            $branch_id = $this->input->post('branch');
            $staff = $this->input->post('staff');
            $date_from = $this->input->post('date_from');
            $date_to = $this->input->post('date_to');
            
            
            $data['staff'] = $this->Staff_model->get_staffbyBranch($branch_id);
            $data['branch'] = $this->Branch_model->get_all_branch();
            // $data['all_rating_category'] = $this->Rating_category_model->get_all_rating_category();
            $data['ratings'] = $this->Rating_model->get_all_ratings_report($branch_id,$staff,$date_from,$date_to);
            $data['branch_id'] = $branch_id;
            $data['date_from'] = $date_from;
            $data['date_to'] = $date_to;
            $data['staff_id'] = $staff;
            // print_r($staff);
            // exit;
